var test = require('tap').test;
var trumpet = require('../');
var fs = require('fs');

test('supports a mapping DSL', function (t) {
    t.plan(1);
    var html = fs.readFileSync(__dirname + '/big_target.html', 'utf8');
    
    var tr = trumpet();
    fs.createReadStream(__dirname + '/big_source.html').pipe(tr);

    tr.map({
        '#page_nav a': function(html) {
            console.log('html', html);
            return html.toUpperCase();
        }
    });


    var data = '';
    tr.on('data', function (buf) { data += buf; });
    
    tr.on('end', function () {
        t.equal(data, '');
    });
});
